import '../styles/global.css'
import '../styles/courses.css'
import React, { useContext, useEffect, useState } from "react";
import Sidemenu from "../components/Sidemenu";
import ToDo from "../components/ToDo";
import PageContent from "../components/PageContent";
import RightSide from "../components/RightSide";
import { AuthContext } from "./auth/AuthProvider";


function Dashboard() {
    const { user } = useContext(AuthContext)
    const [info, setInfo] = useState("")
    useEffect(() => {
        const fetchData = async () => {
            try {
                // const response = await fetch(`http://localhost:8080/api/students/courses/${userInfo.student_id}/`)
                const response = await fetch(`http://localhost:8080/api/students/get/${user.student_id}/`,{
                    method: 'GET',
                    headers: {
                        'Content-Type': 'application/json',
                        'Authorization': `Token ${user.token}`
                    },
                })
                const data = await response.json();
                setInfo(info);
            } catch (error) {
                console.error('Error fetching student data:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <div className="iac-app">
            <div className="iac-layout-columns">
                <div className="iac-main-app-content">
                    <div className="iac-main-content-wrapper">
                        <div className="iac-main-content">
                            <PageContent
                                contentTitle={user ? `${user.first_name} ${user.last_name}'s dashboard` : 'Loading...'}
                            // richContent={richContent}
                            />
                        </div>
                    </div>
                    {/* <div className="iac-main-right-side-wrapper">
                        <RightSide />
                    </div> */}
                </div>
            </div>
        </div>
    )
}

export default Dashboard
