import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';
import "../../styles/global.css"
import "../../styles/modules.css"
import Sidemenu from "../../components/Sidemenu";
import Collapsible from "../../components/Collapsible";


function Modules() {
    const { courseID } = useParams();
    const [modules, setModules] = useState([]);

    useEffect(() => {
        // Fetch modules data from your API
        const fetchModules = async () => {
            try {
                const response = await fetch(`http://localhost:8080/api/courses/${courseID}/modules`, );
                const data = await response.json();
                console.log(data)
                setModules(data);
            } catch (error) {
                console.error('Error fetching modules data', error);
            }
        };

        fetchModules();
    }, [courseID]);

    return (
        <div className="iac-app">
            <div className="iac-layout-columns">
                <div className="iac-main-app-content">
                    <Sidemenu
                        courseID={courseID}
                    />
                    <div className="iac-main-content-wrapper">
                        <div className="iac-main-content">
                            <div className="header-bar"></div>
                            <div className="item-group-container">
                                <div className="context-modules">
                                {modules.map(module => (
                                        <Collapsible
                                            key={module.module_id}
                                            title={module.module_description}
                                            files={module.files}
                                        />
                                    ))}
                                    {/* <Collapsible />
                                    <Collapsible />
                                    <Collapsible />
                                    <Collapsible /> */}
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Modules
